#!/usr/bin/env node
const Redis = require('ioredis');

async function testRedisConnection() {
  const redisUrl = process.env.REDIS_URL || 'redis://localhost:6379';
  console.log(`ðŸ” Testing Redis connection: ${redisUrl}`);

  try {
    const redis = new Redis(redisUrl);
    await redis.ping();
    console.log('âœ… Redis connection successful!');

    // Test basic operations
    await redis.set('myMCP:test', 'connection-verified');
    const value = await redis.get('myMCP:test');
    console.log(`ðŸ“ Test value: ${value}`);

    await redis.del('myMCP:test');
    await redis.quit();
    console.log('ðŸŽ‰ Redis is ready for myMCP!');
  } catch (error) {
    console.error('âŒ Redis connection failed:', error.message);
    process.exit(1);
  }
}

testRedisConnection();

#!/usr/bin/env node
const { spawn } = require('child_process');

async function testMCPServer() {
  console.log('ðŸš€ Testing MCP Server Protocol...');

  const server = spawn('node', ['packages/mcpserver/dist/index.js'], {
    stdio: ['pipe', 'pipe', 'inherit']
  });

  // Test initialize message
  const initMessage = {
    jsonrpc: '2.0',
    id: 1,
    method: 'initialize',
    params: {
      protocolVersion: '2024-11-05',
      capabilities: {},
      clientInfo: { name: 'test-client', version: '1.0.0' }
    }
  };

  server.stdin.write(JSON.stringify(initMessage) + '\n');

  server.stdout.on('data', (data) => {
    console.log('ðŸ“¨ Server response:', data.toString());
  });

  setTimeout(() => {
    server.kill();
    console.log('âœ… MCP Server test completed');
  }, 3000);
}

testMCPServer();

#!/usr/bin/env node
const { exec } = require('child_process');
const chalk = require('chalk');

async function runIntegrationTests() {
  console.log(chalk.blue('ðŸ§ª myMCP Integration Test Suite'));
  console.log('=' .repeat(50));

  const tests = [
    { name: 'Health Checks', command: 'npm run test:health' },
    { name: 'API Integration', command: 'npm run test:api' },
    { name: 'MCP Protocol', command: 'npm run test:mcp' },
    { name: 'Redis Operations', command: 'npm run test:redis' },
    { name: 'End-to-End Workflows', command: 'npm run test:e2e' }
  ];

  let passed = 0;
  let failed = 0;

  for (const test of tests) {
    console.log(chalk.yellow(`\nðŸ” Running ${test.name}...`));
    try {
      await new Promise((resolve, reject) => {
        exec(test.command, (error, stdout, stderr) => {
          if (error) reject(error);
          else resolve({ stdout, stderr });
        });
      });
      console.log(chalk.green(`âœ… ${test.name} passed`));
      passed++;
    } catch (error) {
      console.log(chalk.red(`âŒ ${test.name} failed: ${error.message}`));
      failed++;
    }
  }

  console.log(chalk.blue('\nðŸ“Š Test Results Summary'));
  console.log(`âœ… Passed: ${passed}`);
  console.log(`âŒ Failed: ${failed}`);
  console.log(failed === 0 ? chalk.green('ðŸŽ‰ All tests passed!') : chalk.red('âš ï¸ Some tests failed'));
}

runIntegrationTests();

module.exports = {
  apps: [
    {
      name: 'myMCP-engine',
      script: 'packages/engine/dist/index.js',
      instances: 'max',
      exec_mode: 'cluster',
      env: {
        NODE_ENV: 'production',
        PORT: 3000,
        REDIS_URL: process.env.REDIS_URL,
        ENGINE_ID: 'production-primary',
        IS_PRIMARY: true
      },
      error_file: './logs/engine-error.log',
      out_file: './logs/engine-out.log',
      log_file: './logs/engine-combined.log',
      time: true
    },
    {
      name: 'myMCP-mcpserver',
      script: 'packages/mcpserver/dist/index.js',
      instances: 1,
      env: {
        NODE_ENV: 'production',
        ENGINE_BASE_URL: 'http://localhost:3000',
        DEFAULT_PLAYER_ID: 'production-player'
      },
      error_file: './logs/mcp-error.log',
      out_file: './logs/mcp-out.log',
      time: true
    }
  ]
};

#!/usr/bin/env node
const express = require('express');
const Redis = require('ioredis');
const os = require('os');

const app = express();
const redis = new Redis(process.env.REDIS_URL);

// Performance metrics endpoint
app.get('/metrics', async (req, res) => {
  const metrics = {
    timestamp: new Date().toISOString(),
    system: {
      uptime: os.uptime(),
      loadavg: os.loadavg(),
      memory: {
        total: os.totalmem(),
        free: os.freemem(),
        used: os.totalmem() - os.freemem()
      },
      cpu: os.cpus().length
    },
    redis: {
      connected: redis.status === 'ready',
      memory: await redis.memory('usage').catch(() => 'unknown'),
      keyspace: await redis.info('keyspace').catch(() => 'unknown')
    },
    application: {
      activeConnections: global.activeConnections || 0,
      requestsPerMinute: global.requestsPerMinute || 0,
      averageResponseTime: global.averageResponseTime || 0
    }
  };

  res.json(metrics);
});

app.listen(3001, () => {
  console.log('ðŸ“Š Performance monitoring dashboard running on port 3001');
});
